import React from 'react';
import { Row, Col, Icon, Tabs, Button, Modal, Layout, Badge, Table, Descriptions } from 'antd';
import 'antd/dist/antd.css';
import bg1 from '../../img/background1.png';
import mark from '../../img/校徽实体.png'

const { Header, Footer, Sider, Content } = Layout;
class Admin extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            num:150,
            depart: "计算机类",
            average:90,
            numOfDone:80
        }
        this.logout = this.logout.bind(this);
        this.get=this.get.bind(this);
    }
    async get(){

    }
    logout() {
        this.props.setState({
            isWelcome: true,
            isLogin: false,
            isStudent: false,
            isAllDone: false,
            isAdmin: false,
            isTeacher: false,
            host: "",
            userInfo:
            {
                name: '',
                username: "",
                token: '',
                access: -1,
                score: -1
            },
            answer: {
                choice: {},
                true_false: {}
            },
        })
    }
    componentWillMount() {
        this.get();
    }
    render() {
        return (
            <React.Fragment>
                <Layout>
                    <Header>
                        <Row>
                            <Col span={12}>
                                <h1 style={{ color: 'white', fontSize: "25px" }}><img src={mark} height="45px" width="45px" />&nbsp;校史校情知识竞赛管理系统</h1>
                            </Col>
                            <Col span={2} offset={10}>
                                <Button ghost type="primary" onClick={this.logout}><Icon type="logout" />退出</Button>
                            </Col>
                        </Row>
                    </Header>
                    <Content>
                        <Row>
                            <Col span={22} offset={1}>
                        <Descriptions bordered title={
                            <Row>
                                <Col span={5}><p style={{fontSize:"30px", marginTop:'20px'}}>{this.state.depart}&nbsp;统计信息</p></Col>
                                <Col span={2} offset={1}>
                                    <div style={{marginTop:'20px'}}><Button type="primary" size="large">学生注册</Button></div>
                                    
                                </Col>
                                <Col span={2} offset={0}>
                                <div style={{marginTop:'20px'}}><Button type="primary" size="large">学生修改密码</Button></div>
                                </Col>
                            </Row>
                        }>
                            <Descriptions.Item label="信息范围">{this.state.depart}</Descriptions.Item>
                            <Descriptions.Item label="总人数">{this.state.num}</Descriptions.Item>
                            <Descriptions.Item label="已完成人数">{this.state.numOfDone}</Descriptions.Item>
                            <Descriptions.Item label="平均分">{this.state.average}</Descriptions.Item>
                        </Descriptions>
                        </Col>
                        </Row>
                    </Content>
                </Layout>
            </React.Fragment>
        )
    }
}

export default Admin
