import React from 'react';
import 'antd/dist/antd.css';
import { Card, Row, Col, Layout, Icon, Radio } from 'antd';
import './Grades.css';

import BG from '../../img/图片2.jpg'

const { Header, Footer, Sider, Content } = Layout;
const RadioGroup = Radio.Group;

class Grades extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            question: [],
            focusOn: 0
        }
        for (let i = 0; i < 20; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "选择题",
                title: "",
                choice: [{}, {}, {}, {}],
                isRight: false,
                value: 1,
                answer:2,
            });
        }
        for (let i = 20; i < 30; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "判断题",
                title: "",
                choice: ['√', '×'],
                isRight: false,
                value: 1,
                answer:0,
            });
        }
        this.state.question[0] = {
            isFinish: false,
            kind: "选择题",
            title: "燃煤联合循环发电技术由哪个研究所长期研究计划",
            choice: [{ text: '东大建筑与环境研究所', value: 2 }, { text: '东大热能工程研究所', value: 3 }, { text: '东大能源与环境工程研究所', value: 1 }, { text: '东大动力研究所', value: 4 }],
            isRight: false,
            value: 1,
            answer:2,
        }
        this.state.question[1] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: true
        }
        this.state.question[2] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[3] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[4] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[5] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[6] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[7] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[8] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[9] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[10] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[11] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: [{ text: '1902', value: 2 }, { text: '1988', value: 3 }, { text: '1905', value: 1 }, { text: '1900', value: 4 }],
            isRight: false
        }
        this.state.question[20] = {
            isFinish: false,
            kind: "判断题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['√', '×'],
            isRight: false
        }
        this.state.question[21] = {
            isFinish: false,
            kind: "判断题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['√', '×'],
            isRight: false
        }
        this.state.question[22] = {
            isFinish: false,
            kind: "判断题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['√', '×'],
            isRight: false
        }
    }
    onmouseover(i){
        this.setState({focusOn:i})
    }
