import React from 'react';
import { Row, Col, Icon, Tabs, Button, Modal, Layout } from 'antd';
import 'antd/dist/antd.css';
import './Test.css';
import bg1 from '../../img/background1.png';
import bg2 from '../../img/background2.png';
import bg3 from '../../img/background3.png';
import bg4 from '../../img/background4.jpg';
import bg5 from '../../img/background5.jpg';
import bg6 from '../../img/background6.jpg';
import bg7 from '../../img/background7.jpg';
import bg8 from '../../img/background8.jpg';
import bg9 from '../../img/background9.jpg';
import bg10 from '../../img/background10.jpg';
import bg11 from '../../img/background11.jpg';
import bg12 from '../../img/background12.jpg';


import Title from '../../img/标题.PNG';
import Timer from '../Timer/Timer';
import Choice from '../Choice/Choice';
import TrueFalse from '../TrueFalse/TrueFalse';

let imgs = [bg1, bg2, bg3, bg4, bg5, bg6, bg7, bg8, bg9, bg10, bg11, bg12];
const { TabPane } = Tabs;
const { Header, Footer, Sider, Content } = Layout;

class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            usernanme: "",
            isTesting: false,
            isPaperGet: false,
            isAllDone: false,
            settingVisible: false,
            focusOn: 0,
            pic: 0,
            question: []
        }
        for (let i = 0; i < 20; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "选择题",
                title: "",
                choice: ['', '', '', ''],
                isRight: false
            });
        }
        for (let i = 20; i < 30; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "判断题",
                title: "",
                choice: ['√', '×'],
                isRight: false
            });
        }
        this.openControl = this.openControl.bind(this);
        this.closeControl = this.closeControl.bind(this);
        this.logout = this.logout.bind(this);
        this.done = this.done.bind(this);
        this.Next = this.Next.bind(this);
        this.Prev = this.Prev.bind(this);
        this.submit = this.submit.bind(this);
        //测试初始化
        this.state.question[0] = {
            isFinish: false,
            kind: "选择题",
            title: "燃煤联合循环发电技术由哪个研究所长期研究计划",
            choice: ['东大建筑与环境研究所', '东大热能工程研究所', '东大能源与环境工程研究所', '东大动力研究所'],
            isRight: false
        }
        this.state.question[1] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['1902', '1988', '1905', '1900'],
            isRight: false
        }
        this.state.question[2] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['1902', '1988', '1905', '1900'],
            isRight: false
        }
        this.state.question[20] = {
            isFinish: false,
            kind: "判断题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['√', '×'],
            isRight: false
        }
        this.state.question[21] = {
            isFinish: false,
            kind: "判断题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['√', '×'],
            isRight: false
        }
        this.state.question[22] = {
            isFinish: false,
            kind: "判断题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['√', '×'],
            isRight: false
        }
    }
    Next() {
        let x = this.state.focusOn;
        x++;
        if (x < 30) {
            this.setState({ focusOn: x })
        }
    }
    Prev() {
        let n = this.state.focusOn;
        n--;
        if (n >= 0) {
            this.setState({ focusOn: n })
        }
    }
    componentWillMount() {//设定背景定时；获取试卷
        this.timer = setInterval(() => {
            let x = (this.state.pic + 1) % 4;
            this.setState({ pic: x });
        },
            3000)
    }
    componentWillUnmount() {
        clearInterval(this.timer);
    }
    openControl() {
        this.setState({ settingVisible: true });
    }
    closeControl() {
        this.setState({ settingVisible: false });
    }
    logout() {
        this.props.setState({
            isWelcome: true,
            isLogin: false,
            isStudent: false
        })
    }
    submit() {
        this.setState({ isAllDone: true })
    }
    done(i) {
        let x = this.state.question;
        x[i].isFinish = true;
        this.setState({ question: x });
    }
    render() {
        if (!this.state.isTesting) {
            return (
                <div style={{
                    backgroundImage: `url(${imgs[0]})`,
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                    width: "100%", height: "100%",
                    position: "absolute",
                    top: "0px",
                    bottom: "0px"
                }}>
                    <Modal
                        visible={!this.state.isTesting}
                        title="答题须知"
                        centered={true}
                        footer={[
                            <Button type="primary" onClick={() => {
                                this.setState({ isTesting: true })
                            }}>
                                开始答题
                      </Button>
                        ]}>
                        <p>1.本答题共有30道题</p>
                    </Modal>
                </div>
            )
        }
        return (
            <React.Fragment>
                <Layout>
                    <Header>
                        <h1 style={{color:'white'}}>东南大学校史校情知识竞赛</h1>
                    </Header>
                    <Content style={{backgroundColor: '#1C1C1C'}}>
                        <Row>
                            <Col span={2}>
                                
                            </Col>
                            <Col span={20}>
                            <Tabs activeKey={`${this.state.focusOn}`}
                            animated='true'
                            onTabClick={(x) => { this.setState({ focusOn: x }) }}
                            tabBarGutter='0'
                            tabPosition="bottom"
                            size="small">
                            {this.state.question.map((x, i) => (
                                <TabPane tab={!x.isFinish ? <div>{i + 1}</div> : <div style={{ backgroundColor: '#572A3F', color: 'white', borderRadius: 20 }}>{i + 1}</div>}
                                    key={i}
                                    onChange={() => { this.done(i) }}
                                    style={{transition: "all 1s ease-out"}}
                                    transition
                                >
                                    {x.kind == "选择题" ?
                                        <Choice className="choice" Id={i} state={x} setFinish={this.done.bind(this)} Next={this.Next} Prev={this.Prev} />
                                        : <TrueFalse Id={i} state={x} setFinish={this.done.bind(this)} Next={this.Next} Prev={this.Prev} />
                                    }
                                </TabPane>))
                            }
                        </Tabs>
                            </Col>
                            <Col span={2}>

                            </Col>
                        </Row>
                        
                    </Content>
                    <Footer style={{ textAlign: 'center' }}>Footer</Footer>
                </Layout>
                {/* <body style={{ backgroundImage: `url(${imgs[(this.state.focusOn % 3)]})`, backgroundSize: 'cover', backgroundRepeat: 'no-repeat', backgroundPosition: 'center', transition: '2s' }}>
                    <header>
                        <Row>
                            <Col span={1}></Col>
                            <Col span={1}>
                                <br />
                                <br />
                                <Icon style={{ color: 'white', fontSize: '30px' }} type="home" onClick={this.logout} block={true} />
                            </Col>
                            <Col span={16}>
                                <img src={Title}></img>
                            </Col>
                            <Col span={6}>
                                <br />
                                <br />
                                <Timer state={this.state} setState={this.setState.bind(this)} />
                            </Col>
                        </Row>
                    </header>
                    <Row>
                    <Col span={18} >
                        <div className="Question">
                                <Tabs activeKey={`${this.state.focusOn}`}
