  register() {
    //注册函数
    let that = this;
    this.setState({
      register: {
        post: true
      }
    });
    fetch("http://" + this.props.state.host + "/api/ui/register", {
      method: 'POST',
      mode: 'cors',
      headers: {
        "authorization": that.props.state.token,
        "Content-Type": "application/json"
      },
