    constructor(props) {
        super(props);
        this.state = {
            isTesting: false,
            isPaperGet: false,
            isAllDone: false,
            focusOn: 0,
            pic: 0,
            question:[],
        }
        for (let i = 0; i < 20; i++) {
          this.state.question.push({
              isFinish: false,
              kind: "选择题",
              title: "",
              choice: [{}, {}, {}, {}],
              value: -1
          });
      }
      for (let i = 20; i < 30; i++) {
          this.state.question.push({
              isFinish: false,
              kind: "判断题",
              title: "",
              choice: ['√', '×'],
              value: -1
          });
      }
        this.logout = this.logout.bind(this);
        this.done = this.done.bind(this);
        this.Next = this.Next.bind(this);
        this.Prev = this.Prev.bind(this);
        this.submit = this.submit.bind(this);

        //测试初始化
       testQuestion.question.forEach((x,i)=>{
          this.state.question[i]=x
       })
    }
    Next() {
        let x = this.state.focusOn;
        x++;
        if (x < 30) {
            this.setState({ focusOn: x })
        }
    }
    Prev() {
        let n = this.state.focusOn;
        n--;
        if (n >= 0) {
            this.setState({ focusOn: n })
        }
    }
    Random(arr) {
        let length = arr.length,
            randomIndex,
            temp;
        while (length) {
            randomIndex = Math.floor(Math.random() * (--length));
            temp = arr[randomIndex];
            arr[randomIndex] = arr[length];
            arr[length] = temp
        }
        return arr;
    }
    componentWillMount() {
      //试卷获取
      this.setState({isPaperGet:true})
