import React from 'react';
import { Row, Col, Layout, Carousel, Radio, Steps, Button, message, Tabs, Anchor, Calendar } from 'antd';
import 'antd/dist/antd.css';
import './Test.css';
import bg1 from '../../img/答题1.jpg';
import bg2 from '../../img/答题2.jpg';
import bg3 from '../../img/答题3.jpg';
import bg4 from '../../img/答题4.jpg';

var imgs = [bg1, bg2, bg3, bg4];
const { Header, Footer, Sider, Content } = Layout;
const RadioGroup = Radio.Group;
const Step = Steps.Step;
const { Link } = Anchor;
const { TabPane } = Tabs;

const steps = [{
    title: 'First',
    content: 'diyiti',
  }, {
    title: 'Second',
    content: 'Second-content',
  }, {
    title: 'Last',
    content: 'Last-content',
  }];

class Test extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            usernanme: "",
            isTesting: false,
            isPaperGet: false,
            isAllDone: false,
            settingVisible: false,
            focusOn: 0,
            pic:0,
            question: []
        }
        for (let i = 0; i < 20; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "选择题",
                title:"",
                choice: ['','','',''],
                isRight: false
            });
        }
        for (let i = 20; i < 30; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "判断题",
                title:"",
                choice: ['√','×'],
                isRight: false
            });
        }
        this.openControl = this.openControl.bind(this);
        this.closeControl= this.closeControl.bind(this);
        this.logout = this.logout.bind(this);
        this.done = this.done.bind(this);
        this.Next=this.Next.bind(this);
        //测试初始化
        this.state.question[0]={
            isFinish: false,
            kind: "选择题",
            title:"燃煤联合循环发电技术由哪个研究所长期研究",
            choice: ['东大建筑与环境研究所','东大热能工程研究所','东大能源与环境工程研究所','东大动力研究所'],
            isRight: false
        }
    }
    Next(){
        let x=this.state.focusOn;
        x++;
        if(x<30){
            this.setState({focusOn:x})
        }
    }
    componentWillMount(){//设定背景定时；获取试卷
        this.timer=setInterval(()=>{
            let x=(this.state.pic+1)%4;
            this.setState({pic:x});},
            3000)
    }
    componentWillUnmount(){
        clearInterval(this.timer);
    }
    openControl() {
        this.setState({ settingVisible: true });
    }
    closeControl() {
        this.setState({ settingVisible: false });
    }
    logout() {
        this.props.setState({
            isWelcome: true,
            isLogin: false,
            isStudent: false
        })
    }
    done(i) {
        let x = this.state.question;
        x[i].isFinish = true;
        this.setState({ question: x });
    }
    render(){
        const radioStyle = {
            display: 'block',
            height: '30px',
            lineHeight: '30px',
          };
        return(
        <React.Fragment>
            <Layout>
                <Header style={{verticalAlign:'center', height: 100, backgroundColor:'#006666'}}><h1 style={{color:'white'}}>2019东南大学校史校情知识竞赛答题</h1></Header>
                <Layout>
                    <Content style={{backgroundColor:'white'}}>
                        <Row>
                            <Col span={6} style = {{backgroundImage:`url(${imgs[this.state.x]})`, height:900}}>
                                <div style={{ width: 375, border: '1px solid #d9d9d9', borderRadius: 4 }}>
                                    <Calendar fullscreen={false}  />
                                </div>
                            </Col>
                            <Col span={18}>
                                <Tabs defaultActiveKey="1" tabPosition='left' style={{ height: 720 }}>
                                    {[...Array(30).keys()].map(i => (
                                        <TabPane tab={`Q ${i+1}`} key={i+1}>
                                            <h2>第{i+1}题 燃煤联合循环发电技术由哪个研究所长期研究</h2>
                                            <Radio.Group onChange={this.onChange} value={this.state.value}>
                                                <Radio style={radioStyle} value={1}>
                                                A 东大建筑与环境研究所
                                                </Radio>
                                                <Radio style={radioStyle} value={2}>
                                                B 东大热能工程研究所
                                                </Radio>
                                                <Radio style={radioStyle} value={3}>
                                                C 东大能源与环境工程研究所
                                                </Radio>
                                                <Radio style={radioStyle} value={4}>
                                                D 东大动力研究所
                                                </Radio>
                                            </Radio.Group>
                                        </TabPane>
                                    ))}
                                </Tabs>
                            </Col>
                        </Row>
                    </Content>
                </Layout>
                <Footer>Footer</Footer>
            </Layout>        
        </React.Fragment>
        )
    }
}

export default Test
