import React from 'react';
import { Row, Col, Icon, Tabs, Button, Modal } from 'antd';
import 'antd/dist/antd.css';
import './Test.css';
import bg1 from '../../img/答题1.png';
import bg2 from '../../img/答题2.png';
import bg3 from '../../img/答题3.png';
import Title from '../../img/标题.PNG';
import Timer from '../Timer/Timer';
import Choice from '../Choice/Choice';
import TrueFalse from '../TrueFalse/TrueFalse';

let imgs = [bg1, bg2, bg3];
const { TabPane } = Tabs;

class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            usernanme: "",
            isTesting: false,
            isPaperGet: false,
            isAllDone: false,
            settingVisible: false,
            focusOn: 0,
            pic: 0,
            question: []
        }
        for (let i = 0; i < 20; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "选择题",
                title: "",
                choice: ['', '', '', ''],
                isRight: false
            });
        }
        for (let i = 20; i < 30; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "判断题",
                title: "",
                choice: ['√', '×'],
                isRight: false
            });
        }
        this.openControl = this.openControl.bind(this);
        this.closeControl = this.closeControl.bind(this);
        this.logout = this.logout.bind(this);
        this.done = this.done.bind(this);
        this.Next = this.Next.bind(this);
        this.Prev = this.Prev.bind(this);
        this.submit = this.submit.bind(this);
        //测试初始化
        this.state.question[0] = {
            isFinish: false,
            kind: "选择题",
            title: "燃煤联合循环发电技术由哪个研究所长期研究",
            choice: ['东大建筑与环境研究所', '东大热能工程研究所', '东大能源与环境工程研究所', '东大动力研究所'],
            isRight: false
        }
        this.state.question[1] = {
            isFinish: false,
            kind: "选择题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['1902', '1988', '1905', '1900'],
            isRight: false
        }
        this.state.question[20] = {
            isFinish: false,
            kind: "判断题",
            title: "东南大学的历史最早可追溯至哪一年?",
            choice: ['√', '×'],
            isRight: false
        }
    }
    componentDidMount() {
        const elPrevButton = document.querySelector('#prev');
        const elNextButton = document.querySelector('#next');
        // const flipping = new Flipping();

        const elImages = Array.from(document.querySelectorAll('.ui-big-image'));
        const elThumbnails = Array.from(document.querySelectorAll('.ui-thumbnail'));

        let state = {
            photo: 0
        };

        function send(event) {
            // read cuticle positions
            // flipping.read();

            const elActives = document.querySelectorAll('[data-active]');

            Array.from(elActives)
                .forEach(el => el.removeAttribute('data-active'));

            switch (event) {
                case 'PREV':
                    state.photo--;
                    // Math.max(state.photo - 1, 0);
                    break;
                case 'NEXT':
                    state.photo++;
                    // Math.min(state.photo + 1, elImages.length - 1);
                    break;
                default:
                    state.photo = +event;
                    break;
            }

            var len = elImages.length;
            // Loop Around
            //state.photo = ( ( state.photo % len) + len ) % len;
            state.photo = Math.max(0, Math.min(state.photo, len - 1));

            Array.from(document.querySelectorAll(`[data-key="${state.photo}"]`))
                .forEach(el => {
                    el.setAttribute('data-active', true);
                });

            // execute the FLIP animation
            // flipping.flip();
        }

        elThumbnails.forEach(thumb => {
            thumb.addEventListener('click', () => {
                send(thumb.dataset.key);
            });
        });

        elPrevButton.addEventListener('click', () => {
            send('PREV');
        });

        elNextButton.addEventListener('click', () => {
            send('NEXT');
        });

        send(0);
    }
    Next() {
        let x = this.state.focusOn;
        x++;
        if (x < 30) {
            this.setState({ focusOn: x })
        }
    }
    Prev() {
        let n = this.state.focusOn;
        n--;
        if (n >= 0) {
            this.setState({ focusOn: n })
        }
    }
    componentWillMount() {//设定背景定时；获取试卷
        this.timer = setInterval(() => {
            let x = (this.state.pic + 1) % 4;
            this.setState({ pic: x });
        },
            3000)
    }
    componentWillUnmount() {
        clearInterval(this.timer);
    }
    openControl() {
        this.setState({ settingVisible: true });
    }
    closeControl() {
        this.setState({ settingVisible: false });
    }
    logout() {
        this.props.setState({
            isWelcome: true,
            isLogin: false,
            isStudent: false
        })
    }
    submit() {
        this.setState({ isAllDone: true })
    }
    done(i) {
        let x = this.state.question;
        x[i].isFinish = true;
        this.setState({ question: x });
    }
    render() {
        return (
            <React.Component>
                <div id="app" data-state="0">
                    <div class="ui-big-images">
                        <div class="ui-big-image" data-key="0">
                            <img src="https://picsum.photos/1200/1200/?image=1005" alt="" />
                        </div>
                        <div class="ui-big-image" data-key="1">
                            <img src="https://picsum.photos/1200/1200/?image=804" alt="" />
                        </div>
                    </div>
                    <div class="ui-thumbnails">
                        <div class="ui-thumbnail" tabindex="-1" data-key="0">
                            <img src="https://picsum.photos/1200/1200/?image=1005" alt="" />
                            <div class="ui-cuticle" data-flip-key="cuticle"></div>
                        </div>
                        <div class="ui-thumbnail" tabindex="-1" data-key="1">
                            <img src="https://picsum.photos/1200/1200/?image=804" alt="" />
                            <div class="ui-cuticle" data-flip-key="cuticle"></div>
                        </div>
                    </div>
                    <div class="ui-content">

                        <nav class="ui-nav">
                            <button id="prev" tabindex="-1" title="Previous">&lt;</button>
                            <button id="next" tabindex="-1" title="Next">&gt;</button>
                        </nav>

                        <div class="ui-articles">
                            <article class="ui-article" data-key="0">
                                <h2 class="ui-heading">Stephen Shaw</h2>
                                <p class="ui-paragraph">Lorem ipsum dolor sit amet consectetur adipisicing elit. Beatae cupiditate
						assumenda nemo delectus totam atque quas suscipit dicta.</p>
                            </article>
                            <article class="ui-article" data-key="1">
                                <h2 class="ui-heading">David Khourshid</h2>
                                <p class="ui-paragraph">Lorem ipsum dolor sit amet consectetur adipisicing elit. Quod sapiente
						soluta iusto molestias ullam. </p>
                            </article>
                        </div>
                    </div>
                </div>
            </React.Component>
        )
        // if (!this.state.isTesting) {
        //     return (
        //         <div style={{
        //             backgroundImage: `url(${imgs[0]})`,
        //             backgroundSize: 'cover',
        //             backgroundPosition: 'center',
        //             width: "100%", height: "100%",
        //             position: "absolute",
        //             top: "0px",
        //             bottom: "0px"
        //         }}>
        //             <Modal
        //                 visible={!this.state.isTesting}
        //                 title="答题须知"
        //                 centered={true}
        //                 footer={[
        //                     <Button type="primary" onClick={() => {
        //                         this.setState({ isTesting: true })
        //                     }}>
        //                         开始答题
        //               </Button>
        //                 ]}>
        //                 <p>1.本答题共有30道题</p>
        //             </Modal>
        //         </div>
        //     )
        // }
        // return (
        //     <React.Fragment>
