import React from 'react';
import 'antd/dist/antd.css';
import { Card, Row, Col, Layout,Icon } from 'antd';

import BG from '../../img/图片2.jpg'

const { Header, Footer, Sider, Content } = Layout;
const gridStyle = {
    width: '20%',
    textAlign: 'center',
    fontSize: '20px'
};
class Grades extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

        }
    }
    render() {
        return (
            <React.Fragment>
                <Layout>
                    <Header>
                    <h1 style={{color:"white", textAlign:"center", fontSize:"40px"}}>{this.props.state.userInfo.name}&nbsp;同学，你本次校史校情知识竞赛得分为&nbsp;<b style={{color:"red", fontSize:"50px"}}>{this.props.state.userInfo.score}</b></h1>
                    </Header>
                <Content  style={{ backgroundImage: `url(${BG})` }}>
                    <Row style={{marginTop: 80, marginBottom: 80}}>
                        <Col span={2}>

                        </Col>
                        <Col span={11}>
                    <Card title="答题情况" headStyle={{color:"white"}} style={{backgroundColor:"transparent", color:"white"}}>
                    {this.props.state.question.map((x, i) => (
                        <Card.Grid style={gridStyle}>{i+1}&nbsp;{x.isRight?<Icon type="check-circle" theme="twoTone" twoToneColor="#52c41a" style={{fontSize:"20px"}} />:<Icon type="close-circle" theme="twoTone" twoToneColor="#E83015" style={{fontSize:"20px"}} />}</Card.Grid>))
                    }
                    </Card>
                    </Col>
                    <Col span={4}>

                    </Col>
                    </Row>
                </Content>
                    <Footer style={{ textAlign: 'center' }}>图源：东南大学官微 || 王文虞<br />版权所有©</Footer>
                </Layout>
            </React.Fragment>)
    }
}

export default Grades;
