import React from 'react';
import { Row, Col, Layout, Carousel, Radio, Steps, Button, message, Tabs, Anchor, Calendar } from 'antd';
import 'antd/dist/antd.css';
import './Test.css';
import bg1 from '../../img/答题1.png';
import bg2 from '../../img/答题2.png';
import bg3 from '../../img/答题3.png';
import bg4 from '../../img/答题4.png';

var imgs = [bg1, bg2, bg3, bg4];

class Test extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            usernanme: "",
            isTesting: false,
            isPaperGet: false,
            isAllDone: false,
            settingVisible: false,
            focusOn: 0,
            pic:0,
            question: []
        }
        for (let i = 0; i < 20; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "选择题",
                title:"",
                choice: ['','','',''],
                isRight: false
            });
        }
        for (let i = 20; i < 30; i++) {
            this.state.question.push({
                isFinish: false,
                kind: "判断题",
                title:"",
                choice: ['√','×'],
                isRight: false
            });
        }
        this.openControl = this.openControl.bind(this);
        this.closeControl= this.closeControl.bind(this);
        this.logout = this.logout.bind(this);
        this.done = this.done.bind(this);
        this.Next=this.Next.bind(this);
        //测试初始化
        this.state.question[0]={
            isFinish: false,
            kind: "选择题",
            title:"燃煤联合循环发电技术由哪个研究所长期研究",
            choice: ['东大建筑与环境研究所','东大热能工程研究所','东大能源与环境工程研究所','东大动力研究所'],
            isRight: false
        }
    }
    Next(){
        let x=this.state.focusOn;
        x++;
        if(x<30){
            this.setState({focusOn:x})
        }
    }
    componentWillMount(){//设定背景定时；获取试卷
        this.timer=setInterval(()=>{
            let x=(this.state.pic+1)%4;
            this.setState({pic:x});},
            3000)
    }
    componentWillUnmount(){
        clearInterval(this.timer);
    }
    openControl() {
        this.setState({ settingVisible: true });
    }
    closeControl() {
        this.setState({ settingVisible: false });
    }
    logout() {
        this.props.setState({
            isWelcome: true,
            isLogin: false,
            isStudent: false
        })
    }
    done(i) {
        let x = this.state.question;
        x[i].isFinish = true;
        this.setState({ question: x });
    }
    render(){
        return(
        <React.Fragment>
            <body style={{backgroundImage:`url(${bg1})`, backgroundSize:'cover', backgroundRepeat:'no-repeat', backgroundPosition:'center'}}>
                <header>
                    <h1 style={{color:'white'}}>&nbsp;&nbsp;东南大学校史校情知识竞赛</h1>
                </header>
                <div className="question">
                    <p>问题</p>
                </div>
            </body>       
        </React.Fragment>
        )
    }
}

export default Test
