import React from 'react';
import { Row, Radio, Button, Col } from 'antd';
import 'antd/dist/antd.css';
import bg1 from '../../img/答题1.png';
import bg2 from '../../img/答题2.png';
import bg3 from '../../img/答题3.png';

const RadioGroup = Radio.Group;
let imgs = [bg1, bg2, bg3];
let bg = ['rgba(202,122,44,0.5)', 'rgba(98,89,44,0.5)', 'rgba(159,53,58,0.5)']

class Choice extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: 0
        }
        this.onchange = this.onchange.bind(this);
    }
    onchange(e) {
        this.setState({ value: e.target.value });
        this.props.setFinish(this.props.Id);
        this.props.Next(this.props.Next);
    }
    render() {
        let style = {
            display: 'block',
            height: '50px',
            lineHeight: '50px',
            color: 'white',
            fontSize: '20px'
        }
        return (
            <React.Fragment>
                <Row style={{ backgroundImage: `url(${imgs[(this.props.Id % 3)]})`,
                            backgroundSize: 'cover',
                            backgroundPosition: 'center',
                            width: "100%", height: "100%",
                            marginBottom:40 }}>
                    <Col span={4}></Col>
                    <Col span={16}>
                    <div style={{
                        backgroundColor:bg[this.props.Id % 3 ]
                    }}>
                        <h2 style={{ marginTop: 60, marginBottom: 20, color:'white', fontSize: 25, minHeight: 120 }}>
                            {this.props.state.title}
                        </h2>
                        <Radio.Group style={{ color:'white',marginTop: 40, marginLeft: 40, marginBottom: 40, minHeight: 250 }} onChange={this.onchange} value={this.state.value} buttonStyle={"outline"}>
                            <Radio style={style} value={1}>
                                <b>A</b> &nbsp; {this.props.state.choice[0]}
                            </Radio>
                            <Radio style={style} value={2}>
                                <b>B</b> &nbsp; {this.props.state.choice[1]}
                            </Radio>
                            <Radio style={style} value={3}>
                                <b>C</b> &nbsp; {this.props.state.choice[2]}
                            </Radio>
                            <Radio style={style} value={4}>
                                <b>D</b> &nbsp; {this.props.state.choice[3]}
                            </Radio>
                        </Radio.Group>
                        {/* <Button onClick={this.props.Next}>下一题</Button> */}
                    </div>
                    </Col>
                    <Col span={4}></Col>
                </Row>
            </React.Fragment>
        )
    }
}

export default Choice;
