import React from 'react';
import { Row, Col, Icon, Tabs, Button, Modal, Layout, Badge, Table, Descriptions } from 'antd';
import 'antd/dist/antd.css';
import bg1 from '../../img/background1.png';
import mark from '../../img/校徽实体.png'

const { Header, Footer, Sider, Content } = Layout;
class Admin extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            depart: "计算机学院"
        }
        this.logout = this.logout.bind(this);
    }
    logout() {
        this.props.setState({
            isWelcome: true,
            isLogin: false,
            isStudent: false,
            isAllDone: false,
            isAdmin: false,
            isTeacher: false,
            host: "",
            userInfo:
            {
                name: '',
                username: "",
                token: '',
                access: -1,
                score: -1
            },
            answer: {
                choice: {},
                true_false: {}
            },
        })
    }
    componentWillMount() {
    }
    render() {
        return (
            <React.Fragment>
                <Layout>
                    <Header>
                        <Row>
                            <Col span={12}>
                                <h1 style={{ color: 'white', fontSize: "25px" }}><img src={mark} height="45px" width="45px" />&nbsp;校史校情知识竞赛管理系统</h1>
                            </Col>
                            <Col span={2} offset={10}>
                                <Button ghost type="primary" onClick={this.logout}><Icon type="logout" />退出</Button>
                            </Col>
                        </Row>
                    </Header>
                    <Content>
                        <Descriptions bordered title={
                            <Row>
                                <Col span={12}>{this.state.depart}&nbsp;统计信息</Col>
                                <Col span={2} offset={4}>
                                    <Button type="primary" size="large">学生注册</Button>
                                </Col>
                                <Col span={2} offset={2}>
                                    <Button type="primary" size="large">学生修改密码</Button>
                                </Col>
                            </Row>
                        }>

                        </Descriptions>
                    </Content>
                </Layout>
            </React.Fragment>
        )
    }
}

export default Admin
